"use strict";

require("../common");

var _steps = require("cypress-cucumber-preprocessor/steps");

(0, _steps.Given)('a MultiSelect with a disabled option and onChange handler is rendered', () => {
  cy.visitStory('MultiSelect', 'With disabled option and onChange');
});
(0, _steps.Given)('a MultiSelect with custom options and onChange handler is rendered', () => {
  cy.visitStory('MultiSelect', 'With custom options and onChange');
});
(0, _steps.When)('an option is clicked', () => {
  cy.contains('option one').click();
});
(0, _steps.When)('the selected option is clicked', () => {
  cy.get('[data-test="dhis2-uicore-layer"]').contains('option one').click();
});
(0, _steps.When)('another option is clicked', () => {
  cy.contains('option two').click();
});
(0, _steps.When)("the chip's X is clicked", () => {
  cy.get('[data-test="dhis2-uicore-chip-remove"]').click();
});
(0, _steps.When)('the disabled option is clicked', () => {
  cy.contains('disabled option').click();
});
(0, _steps.When)('the selected option is clicked again', () => {
  cy.get('[data-test="dhis2-uicore-multiselectoption"] label').contains('option one').click();
});
(0, _steps.Then)('the clicked option is selected', () => {
  cy.window().should(win => {
    expect(win.onChange).to.be.calledOnce;
    expect(win.onChange).to.be.calledWith({
      selected: ['1']
    });
  });
});
(0, _steps.Then)('the clicked option is selected as well', () => {
  cy.window().should(win => {
    expect(win.onChange).to.be.calledOnce;
    expect(win.onChange).to.be.calledWith({
      selected: ['1', '2']
    });
  });
});
(0, _steps.Then)('the selected option is deselected', () => {
  cy.window().should(win => {
    expect(win.onChange).to.be.calledOnce;
    expect(win.onChange).to.be.calledWith({
      selected: []
    });
  });
});
(0, _steps.Then)('the onchange handler is not called', () => {
  cy.window().should(win => {
    expect(win.onChange).to.not.be.called;
  });
});
(0, _steps.Then)('the previously selected option is deselected', () => {
  cy.window().should(win => {
    expect(win.onChange).to.be.calledTwice;
    expect(win.onChange).to.be.calledWith({
      selected: []
    });
  });
});