"use strict";

require("../common");

var _steps = require("cypress-cucumber-preprocessor/steps");

(0, _steps.Given)('a filterable MultiSelect with options is rendered', () => {
  cy.visitStory('MultiSelect', 'With filter field');
});
(0, _steps.When)('the user enters a filter string', () => {
  cy.focused().type('one');
});
(0, _steps.When)("the user enters a filter string that doesn't match any options", () => {
  cy.focused().type('does not exist');
});
(0, _steps.Then)('the matching options are displayed', () => {
  cy.contains('option one').should('be.visible');
  cy.contains('option two').should('not.exist');
  cy.contains('option three').should('not.exist');
});
(0, _steps.Then)('the no match text is displayed', () => {
  cy.contains('No match found').should('be.visible');
});