"use strict";

require("../common");

var _steps = require("cypress-cucumber-preprocessor/steps");

(0, _steps.Given)('a MultiSelect with options with a duplicate value and this value is selected', () => {
  cy.visitStory('MultiSelect', 'With duplicate selected option values');
});
(0, _steps.Then)('the first option with the selected value is displayed in the input', () => {
  cy.get('[data-test="dhis2-uicore-select-input"]').should('contain', 'option one');
});
(0, _steps.Then)('both options are highlighted in the dropdown', () => {
  cy.get('input[name="option one"]').should('have.attr', 'checked');
  cy.get('input[name="option one a"]').should('have.attr', 'checked');
});