require("../common");

var _steps = require("cypress-cucumber-preprocessor/steps");

(0, _steps.Given)('a SingleSelect with a disabled option and onChange handler is rendered', () => {
  cy.visitStory('SingleSelect', 'With disabled option and onChange');
});
