"use strict";

require("../common");

var _steps = require("cypress-cucumber-preprocessor/steps");

(0, _steps.Given)('a clearable SingleSelect with a selection and onchange handler is rendered', () => {
  cy.visitStory('SingleSelect', 'With clear button, selection and onChange');
});
(0, _steps.When)('the clear button is clicked', () => {
  cy.get('[data-test="dhis2-uicore-singleselect-clear"]').click();
});
(0, _steps.Then)('the SingleSelect is cleared', () => {
  cy.window().should(win => {
    expect(win.onChange).to.be.calledOnce;
    expect(win.onChange).to.be.calledWith({
      selected: ''
    });
  });
});